export const createSql = {
  addAirport: async(data) => {
    const sql = `INSERT INTO airport VALUES (?, ?, ?, ?)`;
    const [result] = await promisePool.query(sql, [data.Airport_code, data.Name, data.City, data.State]);
    return result[0];
