sourcesContent:
import express from "express";
import { selectSql} from "../../database/sql";

const router = express.Router();

router.get('/', async function (req, res) {
    if (req.session.user == undefined) {
        res.redirect('/');
    } else if (req.session.user.role === 'user') {
        const user_id = req.session.user.id;
        console.log(user_id);
        const legInstance = await selectSql.getLegInstance();
        const seatReservation = await selectSql.getSeatReservation(user_id);
        res.render('user', { 
            title1: "legInstance",
            title2: "seatReservation",
            legInstance,
            seatReservation
        });
    } else {
        res.redirect('/user');
    }
});

router.post('/', async function (req, res) {
    if (req.body._method === 'PUT') {
        const vars = req.body;
        const User_id = req.session.user.id;
        console.log(User_id);
        const data = {
            Flight_number: vars.flight_number,
            Leg_number: vars.leg_number,
            Date: vars.date,
            Seat_number: vars.seat_number,
            User_id: User_id,
        }
        await callTransaction.updateSeatReservation(data);
        res.redirect('/user');
    }

    if (req.body._method === 'DELETE') {
        const vars = req.body;
        const data = {
            Flight_number: vars.flight_number,
            Leg_number: vars.leg_number,
            Date: vars.date,
            Seat_number: vars.seat_number,
        }
        await callTransaction.deleteSeatReservation(data);
        res.redirect('/user');
    }

    const vars = req.body;
    const User_id = req.session.user.id;
    console.log(User_id);
    const data = {
        flight_number: vars.flight_number,
        leg_number: vars.leg_number,
        Date: vars.date,
        Seat_number: vars.seat_number,
        User_id: User_id,
    }
    await callTransaction.bookSeat(data);
    res.redirect('/user');
});

module.exports = router;

